function App() {

  return (
    <div className="App p-5">

      <PriceCard />

    </div>
  );
}

function PriceCard(){
let data = {
      plan: "FREE",
      price: "$0/month",
      option: [{value:true, text:"Single User"},{value:true, text:"50GB Storage"},{value:true, text:"Unlimited Public Projects"},{value:true, text:"Community Access"},{value:true, text:"Unlimited Private Projects"},{value:false, text:"Dedicated Phone Support"},{value:false, text:"Free Subdomain"},{value:false, text:"Monthly Status Reports"}]
    }
    // for(let datas of data.option){

    //   console.log(datas.text);
    // }

    // data.option.map((val)=>{
    //   // console.log(val.text);
    //   <li>{val.text}</li>
    // })
let liStyle = {
  color: data.option.value ? "#000" : "#565656"
}
  return(
    <div className='card col-md-3'>
      <div className='card-header'>
        <h5>{data.plan}</h5>
        <h1>{data.price}</h1>
      </div>
      <div className='card-body'>
      <ul>
      {data.option.map((val, ind)=>(
        <li style={{liStyle}}>{val.value? <i class="fa-solid fa-check" style={{color:"#000"}}></i> :<i class="fa-solid fa-xmark"></i>} {val.text}</li>
      ))}
      </ul>
      <div className='btn btn-primary btn-lg rounded-pill'>Button</div>
      </div>


    </div>
  )
}

export default App;


